import styles from './addNewPost.module.css'
import {posts} from "../../data/post.data";
import {useState} from "react";

import setPosts from '../get-post/postList'

function AddNewPost(){
    const [title, setTitle] = useState('');
    const [text, setText] = useState('');

    const createPost = (e) =>{
        e.preventDefault();

        setPosts(prev => [{id: posts.length, title, text}, ...prev]);
    }

    return(
        <form className={styles.addPost}>
            <div className={styles.info}>
                <img src='/images/userIcon.jpg'
                     className={styles.icon}
                     alt="userIcon">
                </img>

                <div className = {styles.input}>
                    <textarea className={styles.title}
                              placeholder="Тема"
                              onChange={e => setTitle}
                              required>
                    </textarea>

                    <textarea className={styles.msg}
                              placeholder = "Пишите здесь"
                              required>
                    </textarea>

                    <button type="submit"
                            className={styles.submitButton}
                            onClick={e => createPost(e)}>
                        Опубликовать
                    </button>
                </div>

            </div>

        </form>
    );
}


export default AddNewPost
