import '../../assets/styles/header.css'

import { useCallback } from "react";
import Particles from "react-tsparticles";
import { loadFull } from "tsparticles";

function Header() {
    const particlesInit = useCallback(async engine => {
        await loadFull(engine);
    }, []);

    const particlesLoaded = useCallback(async container => {
        await container;
    }, []);

    return (
        <div>
            <header>
                Publish your ideas
            <Particles
                style={{ position: "absolute", maxHeight:"100vh", }}
                id="tsparticles"
                init={particlesInit}
                loaded={particlesLoaded}
                options={{
                    fpsLimit: 60,
                    interactivity: {
                        events: {
                            onClick: {
                                enable: true,
                                mode: "push",
                            },
                        },
                        modes: {
                            push: {
                                quantity: 3,
                            },
                            repulse: {
                                distance: 30,
                                duration: 0.5,
                            },
                        },
                    },
                    particles: {
                        color: {
                            value: ["#03dac6", "#ff0266", "#ffa500"],
                        },
                        links: {
                            color: ["#03dac6", "#ff0266", "#ffa500"],
                            distance: 140,
                            enable: true,
                            opacity: 0.4,
                            width: 1,
                        },
                        move: {
                            direction: "none",
                            enable: true,
                            outModes: {
                                default: "bounce",
                            },
                            random: false,
                            speed: 5,
                            straight: false,
                        },
                        number: {
                            density: {
                                enable: true,
                                area: 800,
                            },
                            value: 50,
                        },
                        opacity: 0.2,
                    },

                }}
            />
            </header>
        </div>

    );
}

export default Header;
